ADD JAR hdfs:///user/json-serde-1.3.8-jar-with-dependencies.jar;
set hive.support.sql11.reserved.keywords=false;
CREATE external table tweets_kafk ( text STRING, user STRUCT <screen_name:STRING,friends_count:INT,followers_count:INT>,retweet_count INT,favorite_count INT ) STORED BY 'org.apache.hadoop.hive.kafka.KafkaStorageHandler' TBLPROPERTIES ("kafka.topic" = "foot", "kafka.bootstrap.servers"="master:2181");